          &quot;name&quot;: &quot;icon.png&quot;,
          &quot;contentURL&quot;: &quot;https://aps-rno214011.us.oracle.com/partner/content?contentId=2563185&quot;,
          &quot;mimeType&quot;: &quot;image/png&quot;,
          &quot;fileExtension&quot;: &quot;png&quot;
        },
        &quot;products&quot;: [
          {
            &quot;code&quot;: &quot;compute&quot;,
            &quot;name&quot;: &quot;Compute Cloud&quot;,
            &quot;categories&quot;: [
              {
                &quot;code&quot;: &quot;VIRTUAL_MACHINES&quot;,
                &quot;name&quot;: &quot;Virtual Machines&quot;
              }
            ]
          }
        ],
        &quot;installable&quot;: false,
        &quot;isPrivate&quot;: false
      }
    }
  ],
    &quot;hasMore&quot;: true,
    &quot;totalResults&quot;: 1,
    &quot;totalAvailableRecords&quot;: 31,
    &quot;links&quot;: [       {
            &quot;rel&quot;: &quot;CANONICAL&quot;,
