      &quot;actionExecutionSequence&quot;: [
        {
          &quot;sequenceNumber&quot;: 1,
          &quot;actionTag&quot;: &quot;Delete credential&quot;
        }
      ]
    }
  ],
  &quot;accessURLs&quot;: [
    {
      &quot;name&quot;: &quot;static&quot;,
      &quot;description&quot;: &quot;Test URL&quot;,
      &quot;staticURL&quot;: &quot;https://www.oracle.co.in/?gws_rd=ssl&quot;
