         {
             &quot;mapMode&quot;: &quot;CRM&quot;,
             &quot;mapName&quot;: &quot;SalesCloudContactExport_Release9&quot;,
             &quot;mapType&quot;: &quot;EXPORT&quot;,
             &quot;objectType&quot;: &quot;CONTACT&quot;
         },
